import react, {useState} from "react";
import axios from 'axios';
import { setUserSession } from './Utils/Common';

function Login(props){

    const username = useFormInput("");
    const password = useFormInput("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleLogin = () => {
        setError(null);
        setLoading(true);
        
        axios.post('https://apisim.mps.gov.my/api/mymps/akaunbyic?nokp=930420145231', { username: username.value, password: password.value }).then(response => {

            setLoading(false);
            setUserSession(response.data[0].NOAKAUN, response.data[0].NAMA_PEMILIK);

            console.log(response.data);
            props.history.push('/dashboard');
        })
        .catch(error => {

            setLoading(false);

            console.log("Something went wrong. Plese try again!");
            if (error.response.status === 401) setError(error.response.data.message);
            else setError("Something went wrong. Please try again later.");
        });
    }

    return (
        <div>
            Login <br /><br />
            {/* <div>
                Username <br />
                <input type="text" {...username} />
            </div>
            <div style={{ marginTop: 10 }}>
                Password <br />
                <input type="password" {...password} />
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br /> */}

            
            <div class="flex flex-wrap items-center">
            <div className="container mx-auto">
                <div className="flex flex-wrap justify-center bg-white shadow-xl rounded-lg -mt-64 py-16 px-12 relative z-10">
                    <div className="w-full text-center lg:w-8/12">
                    <p className="text-4xl text-center">
                        <span role="img" aria-label="love">
                        üòç
                        </span>
                    </p>
                    <h3 className="font-semibold text-3xl">
                        Do you love this Starter Kit?
                    </h3>
                    <p className="text-gray-600 text-lg leading-relaxed mt-4 mb-4">
                        Cause if you do, it can be yours now. Hit the buttons below to
                        navigate to get the Free version for your next project. Build a
                        new web app or give an old project a new look!
                    </p>
                    <div className="sm:block flex flex-col mt-10">
                        <a
                        href="https://www.creative-tim.com/learning-lab/tailwind/react/overview/notus?ref=nr-index"
                        target="_blank"
                        className="get-started text-white font-bold px-6 py-4 rounded outline-none focus:outline-none mr-1 mb-2 bg-blue-500 active:bg-blue-600 uppercase text-sm shadow hover:shadow-lg ease-linear transition-all duration-150"
                        >
                        Get started
                        </a>
                        <a
                        href="https://github.com/creativetimofficial/notus-react?ref=nr-index"
                        target="_blank"
                        className="github-star sm:ml-1 text-white font-bold px-6 py-4 rounded outline-none focus:outline-none mr-1 mb-1 bg-gray-800 active:bg-gray-700 uppercase text-sm shadow hover:shadow-lg"
                        >
                        <i className="fab fa-github text-lg mr-1"></i>
                        <span>Help With a Star</span>
                        </a>
                    </div>
                    <div className="text-center mt-16"></div>
                    </div>
                </div>
                </div>
            </div>
        </div>
    )
    
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);
   
    const handleChange = e => {
      setValue(e.target.value);
    }
    return {
      value,
      onChange: handleChange
    }
  }
   
export default Login;
